mapper.select:
select gno, gname, g.tno, tname, price, balance, unit, qperied, weight, g.status from goodsinfo g, goodstype t where g.tno=t.tno

	and gname like concat('%', ?, '%')



	and g.tno = #{tno}



	and balance >= #{start}



	and balance <= #{end}

 order by gno desc limit #{page}, #{rows}

